sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var gr = new GlideRecord('x_820118_lms_leave_calculator'); // here the glide record is taken from the leave calculator table
    gr.addQuery('country.name', current.location.country); // to select the country from the user administration the country tab is selected from leave calculator column
    gr.query(); //it is used to add the data into querry or table
    if (gr.next())
	{
        if (gr.leave_assignment == 'y') //logic created according to the leave calculator
		{ 
            var gdt = new GlideDateTime();
            var currentMonth = gdt.getMonthLocalTime() - 1; //to get the current month value we have used the API method as getMonthLocalTime()/
            var monthsLeft = 12 - currentMonth; //calculate the months left in year
            var perMonth = gr.leaves / 12; // to grt the leaves left in month
            var totalLeaves = perMonth * monthsLeft; //to calculate the total leaves 
            var leaveBucket = new GlideRecord('x_820118_lms_leave_bucket'); // we have added the leave bucket logic to get the vales in LMS
            leaveBucket.initialize();  // to creat or initialize the recod by using (.initialization)//
            leaveBucket.leave_type = gr.leave_type; //populating the leave type field in logic of leave calculator
            leaveBucket.accrued = totalLeaves;  //populate the accrued leaves 
            leaveBucket.balance = totalLeaves;
            leaveBucket.taken = 0;
            leaveBucket.employee = current.sys_id;
            leaveBucket.insert();
        } 
		else if (gr.leave_assignment == 'm') 
		{
            var perMonthM = gr.leaves / 12;
            var leaveBucketM = new GlideRecord('x_820118_lms_leave_bucket');
            leaveBucketM.initialize();
            leaveBucketM.leave_type = gr.leave_type;
            leaveBucketM.accrued = perMonthM;
            leaveBucketM.balance = perMonthM;
            leaveBucketM.taken = 0;
            leaveBucketM.employee = current.sys_id;
            leaveBucketM.insert();
        }
    }



})(current, previous);
